import React, { useEffect, useState } from "react";
import "../../App.css";
import BarChart from "./BarChart";
import { getCampaignAnalytics } from "./../../api/Api";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";

export default class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      total: [],
      total1: [],
      total2: [],
      scheduledCalls: 0,
      totalClients: 0,
      noResponse: 0,
      startDate: new Date(),
      endDate: new Date(),
      start: new Date(),
      end: new Date(),
    };
    this.getCampaignAnalyticsApiReq =
      this.getCampaignAnalyticsApiReq.bind(this);
  }
  componentDidMount() {
    // this.getCampaignAnalyticsApiReq(params);
  }

  async getCampaignAnalyticsApiReq() {
    //scheduledCalls
    //totalClients
    //noResponse
    const paramsTotalClients = {
      type: "totalClients",
      fromTime: this.state.startDate,
      toTime: this.state.endDate,
    };
    await getCampaignAnalytics(paramsTotalClients).then((res) => {
      this.setState({ totalClients: res.data });
    });
    const paramsScheduledCalls = {
      type: "scheduledCalls",
      fromTime: this.state.startDate,
      toTime: this.state.endDate,
    };
    await getCampaignAnalytics(paramsScheduledCalls).then((res) => {
      this.setState({ scheduledCalls: res.data });
    });
    const paramsNoResponse = {
      type: "noResponse",
      fromTime: this.state.startDate,
      toTime: this.state.endDate,
    };
    await getCampaignAnalytics(paramsNoResponse).then((res) => {
      this.setState({ noResponse: res.data });
    });
  }
  setStartDate = (date) => {
    //console.log(date);
    const setStartDate = moment(date).format("DD/MM/YYYY");
    this.setState({ startDate: setStartDate, start: date });
  };
  setEndDate = (date) => {
    const endDate = moment(date).format("DD/MM/YYYY");
    this.setState({ endDate: endDate, end: date });
  };
  render() {
    return (
      <div>
        <div style={{ display: "inline-flex", alignContent: "space-between" }}>
          <div>
            <>
              <h6>From Date:</h6>
              <DatePicker
                selected={this.state.startDate}
                onChange={(date) => this.setStartDate(date)}
                selectsStart
                startDate={this.state.startDate}
                endDate={this.state.endDate}
              />
              <h6>To Date: </h6>
              <DatePicker
                selected={this.state.endDate}
                onChange={(date) => this.setEndDate(date)}
                selectsEnd
                startDate={this.state.start}
                endDate={this.state.endDate}
                minDate={this.state.startDate}
              />
            </>
          </div>
          <button
            onClick={() => this.getCampaignAnalyticsApiReq()}
            className="btn btn-primary"
          >
            Search
          </button>
        </div>
        <div class="page-content fade-in-up px-3 m-2">
          <div class="row mb-4" style={{ display: "inline-flex" }}>
            <div
              class="col-lg-4 col-md-6"
              style={{
                width: "200px",
                height: "140px",
                border: "1px solid",
                margin: "23px",
                padding: "43px",
                backgroundColor: "silver",
              }}
            >
              <div class="card mb-4">
                <div class="card-body flexbox-b">
                  <div>
                    <div class="text-muted">
                      <h6>TotalClients contacted</h6>
                    </div>

                    {this.state.total.map((i) => (
                      <h3 class="font-strong text-success">{i.TotalClients}</h3>
                    ))}
                  </div>
                </div>
              </div>
            </div>
            <div
              class="col-lg-4 col-md-6"
              style={{
                width: "200px",
                height: "140px",
                border: "1px solid",
                margin: "23px",
                padding: "43px",
                backgroundColor: "silver",
              }}
            >
              <div class="card mb-4">
                <div class="card-body flexbox-b">
                  <div>
                    <div class="text-muted">
                      <h6>Total Calls Scheduled</h6>
                    </div>

                    {this.state.total1.map((i) => (
                      <h3 class="font-strong text-primary">
                        {i.scheduledCalls}
                      </h3>
                    ))}
                  </div>
                </div>
              </div>
            </div>
            <div
              class="col-lg-4 col-md-6"
              style={{
                width: "200px",
                height: "140px",
                border: "1px solid",
                margin: "23px",
                padding: "43px",
                backgroundColor: "silver",
              }}
            >
              <div class="card mb-4">
                <div class="card-body flexbox-b">
                  <div>
                    <div class="text-muted">
                      <h6>Number of No Responses</h6>{" "}
                    </div>

                    {this.state.total2.map((i) => (
                      <h3 class="font-strong text-pink">{i.noResponse}</h3>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <BarChart />
      </div>
    );
  }
}
