import React, { useState } from "react";
import './login.css';
import profile from "../../images/gatestone2.png";
import email from "../../images/abc.jpg";
import lock from "../../images/pass.png";
import { useHistory } from "react-router-dom";



export default function Login  ()  {
    const [userName,setuserName] = useState('');
    const [passWord,setpassWord] = useState('');
    const [errorMessage,seterrorMessage] = useState('');

    const history = useHistory();
  
  const routeChange = (e) =>{ 
    switch(e.target.id){
        case 'email':
            setuserName({
                userName:e.target.value,
                errorMessage:""
            })
            break;
         case 'password':
             setpassWord({
                 password:e.target.value,
                 errorMessage:""
             }) 
             break;
             default:
                 return this.state;  
  }

  handleLogin = (event)=>{
    const {userName,password} = useState('');
  event.preventDefault();
 if(userName&&password){
     const reg= /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      if(reg.test(String(userName).toLowerCase())){
          useHistory.push('/products')
      }else{
          setState({
               ...this.state,
               errorMessage:"please enter valid email address" 
          })
      }
 }else{
     this.setState({
         ...this.state,
         errorMessage:"please enter username and password"
     })
 }

}
   return (
       <div className="main">
         <div className="sub-main">
             <div>
                 <div className="imgs">
                     <div className="container-image">
                        <img src={profile} alt="profile" class="profiles"/>

                     </div>
                 </div>
                 <div>
                     <h1>Admin Login</h1>
                     <div>
                         <img src={email} alt="email" className="email" ></img>
                         <input type="text" placeholder="user name" className="name"/>
                     </div>
                     <div className="second-input">
                         <img src={lock} alt="pass" className="email" />
                         <input type="text" placeholder="Password" className="name" />
                     </div>
                     <div className="login-button">
                         <button className="button1" onClick={handleLogin()}>Login</button>
                     </div>
                     
                         <p >
                             <a href="/forgot">Change Password</a>
                         </p>
                    
                 </div>
             </div>

         </div>
       </div>
   ) 
   }

}
