import React, { Component } from "react";
import { Chart } from "react-google-charts";

export default class BarCha extends Component {
  constructor(props) {
    super(props);
    this.state = {
      totalClients: 0,
      scheduledCalls: 0,
      noResponse: 0,
    };
  }
  // static getDerivedStateFromProps(prop, state) {
  //   console.log("props", prop);
  //   console.log("state", state);
  //   if (state.totalClients !== prop.totalClients) {
  //     return {
  //       totalClients: prop.totalClients,
  //     };
  //   }
  // }

  render() {
    const chard = this.props;
    console.log("chart", this.props);
    const data = [
      [
        "Year",
        "No.of TotalClients",
        "No. of Calls Scheduled",
        "No. of No responses",
      ],
      ["Day1", this.props[0], 33, 44],
    ];

    const options = {
      chart: {
        title: "Campaign Analytics",
        subtitle:
          "No.of contacted,  Call scheduled  and No.of No Responses: 2021-2022",
      },
    };

    return (
      <Chart
        chartType="Bar"
        width="80%"
        height="400px"
        data={data}
        options={options}
      />
    );
  }
}
