import React, { Component } from "react";
import { Chart } from "react-google-charts";
import axios from "axios";

export const data = [
  ["Year", "No.of TotalClients", "No. of Calls Scheduled", "No. of No responses"],
  ["Day1", 200, 400, 200],
  ["Day2", 200, 460, 250],
  ["Day3", 660, 100, 300],
  ["Day4", 200, 540, 350],
];

export const options = {
  chart: {
    title: "Campaign Analytics",
    subtitle: "No.of contacted,  Call scheduled  and No.of No Responses: 2021-2022",
  },
};

export default class BarCha extends Component {
  constructor(props){
    super(props)
     this.state={
       data:[]
     }
  }
  componentDidMount(){
    const dataset=[];
    const dataset1=[];
    const article = {  "type":"totalClients"};
    axios.post('https://us-central1-gatestone-yyte.cloudfunctions.net/campaignAnalytics', article)
    .then(response =>{
      for(const v of response.data){
        console.log("v",v.TotalClients)
        dataset.push(v.TotalClients)
      }
      this.setState({ data : [
        ["Year", "No.of TotalClients", "No. of Calls Scheduled", "No. of No responses"],
        ["Day1", dataset],
        ["Day2", dataset],
        ["Day3", 660, 100, 300],
        ["Day4", 200, 540, 350],
      ]})
    })
  }
  render() {
    return (
      <Chart
        chartType="Bar"
        width="80%"
        height="400px"
        data={data}
        options={options}
      />
    );
  }
}
