import React, { useEffect, useState } from "react";
import { getExistingCampaign, sendData } from "../../api/Api";
export default function Consulting() {
  const [data, setData] = useState([]);
  const [selectedData, setSendSelectedData] = useState("");
  const sendSelectedData = () => {
    sendData(selectedData).then((res) => {
      console.log("res", res);
    });
  };
  const onChangeSetData = (e) => {
    setSendSelectedData(e.target.value);
    console.log(e.target.value);
  };
  useEffect(() => {
    getExistingCampaign().then((res) => {
      setData(res.data);
      console.log("res", res);
    });
  }, []);
  return (
    <>
      <div>
        <h1>Existing campaign</h1>
        <select id="dropdown" onChange={onChangeSetData}>
          <option value="N/A">Select</option>
          {data &&
            data.length > 0 &&
            data.map((op, index) => {
              return <option key={index} value={op}>{op}</option>;
            })}
        </select>
        <button onClick={sendSelectedData}>send Data</button>
      </div>
    </>
  );
}
