import React, { useEffect, useState } from 'react';
import '../../App.css';
//import "bootstrap/dist/css/bootstrap.min.css";
import BarChart from './BarChart';
import axios from 'axios';
import {getAllclients} from "../../api/Api";

export default class Products extends React.Component {
   constructor(props){
     super(props);
    this.state= {
       total:[],
       total1:[],
       total2:[],
       date:"",
       date1:""
    }
   }
  componentDidMount(){
    const article = {  "type":"totalClients" ,};
    axios.post('https://us-central1-gatestone-yyte.cloudfunctions.net/campaignAnalytics', article)
    .then(response => this.setState({total:response.data}))

    const article1 = {  "type":"scheduledCalls"};
    axios.post('https://us-central1-gatestone-yyte.cloudfunctions.net/campaignAnalytics', article1)
    .then(response => this.setState({total1:response.data}))

    const article2 = {  "type":"noResponse"};
    axios.post('https://us-central1-gatestone-yyte.cloudfunctions.net/campaignAnalytics', article2)
    .then(response => this.setState({total2:response.data}))

  }
  render(){
    console.log('hhh',this.state.total)
    console.log('hhh',this.state.total1)


  return(
    <div >
    <div style={{display:'inline-flex',alignContent:'space-between'}}>
    <div><h6>From Date:{this.state.date}</h6>
    <input type="date" onChange={(e)=>this.setState({date:e.target.value})}/></div>
    <div><h6>To Date</h6>
    <input type="date" onChange={(e)=>this.setState({date1:e.target.value})}/></div>
    </div>
    <div class="page-content fade-in-up px-3 m-2">
    <div class="row mb-4" style={{display:'inline-flex'}}>
      <div class="col-lg-4 col-md-6" style={{width:'200px',height:'140px',border:'1px solid',margin:'23px',padding:'43px',backgroundColor:'silver'}}>
        <div class="card mb-4">
          <div class="card-body flexbox-b">
            <div>
            <div class="text-muted"><h6>TotalClients contacted</h6></div>

     {this.state.total.map((i)=>(   <h3 class="font-strong text-success">{i.TotalClients}</h3>))}


            </div>
          </div>
        </div>
      </div>
      <div class="col-lg-4 col-md-6" style={{width:'200px',height:'140px',border:'1px solid',margin:'23px',padding:'43px',backgroundColor:'silver'}}>
        <div class="card mb-4">
          <div class="card-body flexbox-b">
            <div>
            <div class="text-muted"><h6>Total Calls Scheduled</h6></div>

            {this.state.total1.map((i)=>( <h3 class="font-strong text-primary">{i.scheduledCalls}</h3>))}


            </div>
          </div>
        </div>
      </div>
      <div class="col-lg-4 col-md-6" style={{width:'200px',height:'140px',border:'1px solid',margin:'23px',padding:'43px',backgroundColor:'silver'}}>
        <div class="card mb-4">
          <div class="card-body flexbox-b">
            <div>
            <div class="text-muted"><h6>Number of No Responses</h6> </div>

            {this.state.total2.map((i)=>(  <h3 class="font-strong text-pink">{i.noResponse}</h3>))}


            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
  <BarChart />
  </div>
  )
}
}
