import React, { Component } from "react";
import { Chart } from "react-google-charts";

export default class BarCha extends Component {
  constructor(props) {
    super(props);
    this.state = {
      totalClients: 22,
      scheduledCalls: 33,
      noResponse: 44,
    };
    getDerivedStateFromProps.bind(this)
  }
  static getDerivedStateFromProps(props, state) {
    return {
      selected: props.selected,
    };
  
    console.log("props", props);
    console.log("state", state);
  }

  render() {
    console.log("chart", this.state);
    const data = [
      [
        "Year",
        "No.of TotalClients",
        "No. of Calls Scheduled",
        "No. of No responses",
      ],
      [
        "Day1",
        this.state.totalClients,
        this.state.scheduledCalls,
        this.state.noResponse,
      ],
    ];

    const options = {
      chart: {
        title: "Campaign Analytics",
        subtitle:
          "No.of contacted,  Call scheduled  and No.of No Responses: 2021-2022",
      },
    };

    return (
      <Chart
        chartType="Bar"
        width="80%"
        height="400px"
        data={data}
        options={options}
      />
    );
  }
}
